package week2day2;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark=360;
		if(mark>400){
			System.out.println("Mark is greater than 400");
		}
		else if(mark>320)
		{
			System.out.println("Mark is greater than 320");
		}
		else
		{
			System.out.println("original mark is" +mark);
		}

	}

}
